import React, { Component } from 'react';
import Like from "./common/like"
import ListGroup from './common/ListGroup'
import Pignation from './common/pagination';
import {getMovies} from "../services/fakeMovieService";
import {getGenres }from "../services/fakeGenreService"
import {paginate }from '../utils/paginate'


class Movies extends Component {
    state = { 
        movies :[],
        genres:[],
        currentPage:1,
        pageSize:4
     }


     componentDidMount(){
         this.setState({movies : getMovies(), genres:getGenres()})
     }

     // handle events
     handleDelete =(movie)=>{
         const movies = this.state.movies.filter(m => m._id !== movie._id);
         this.setState({movies})


     }
     handleLike =(movie)=>{
        const movies =[...this.state.movies];
        const index =movies.indexOf(movie);
        movies[index] ={...movies[index]}
        movies[index].liked =!movies[index].liked ;
        this.setState({movies})
     }
     handlePageChange =(page)=>{
        this.setState({currentPage: page})
     }
     handleGereSelect = genre =>{
         
         this.setState({selectedGenre :genre})
     }

    
    render() 
    { 

        const {length : count} =this.state.movies;
        const {pageSize ,currentPage,selectedGenre ,movies :allMovies}= this.state
        if (count=== 0 ) return<p>There are no movies in database</p>;
        const filtered = selectedGenre ? 
        allMovies.filter(m =>m.genre._id ===selectedGenre._id):allMovies
        // create new list of movie to keep the original one untouched throug pagination ,filtering and search
        const movies = paginate(allMovies,currentPage,pageSize)

        return (<div  className="row">


            <div className="col-3">
                <ListGroup 
                 items ={this.state.genres}
                 selectedItem ={this.state.selectedGenre}
                 onItemSelect={this.handleGereSelect} 
                 />
            </div>

            <div className="col">
            <p>Showing {count} movies in database</p>
               <table className="table">
            <thead>
                <tr>
                    <th>Title</th>
                    <th>Genre</th>
                    <th>Stock</th>
                    <th>Rate</th>
                    <th></th>
                    <th></th>
                </tr>
            </thead>
            <tbody>
                {movies.map(movie => 
                     <tr key={movie._id}>
                    <td>{movie.title}</td>
                    <td>{movie.genre.name}</td>
                    <td>{movie.numberInStock}</td>
                    <td>{movie.dailyRentalRate}</td>
                    <td><Like  liked ={movie.liked}   onClick={()=>this.handleLike(movie)}/></td>
                    <td><button onClick ={()=>this.handleDelete(movie)}
                    className="btn btn-danger btn-sm">Delete</button>  
                    </td>
                </tr>)}

            </tbody>
        </table>
        <Pignation 
         itemCount ={count} 
         pageSize={pageSize}
         currentPage={currentPage}
         onPageChange = {this.handlePageChange} />
            </div>

        </div>)
     
    }
}export default Movies;
